/**
 *
 * @param {string} text - Converts text to fancy fonts
 * @returns string
 */
export function fancy(text: string): string;
/**
 *
 * @param {string} text - flips text to a reverse direction
 * @returns string
 */
export function flipText(text: string): string;
export function fancyText(inputText: any, option?: null): Promise<any>;
